om <- pco %>% 
  broom::tidy(matrix="v") %>% 
  tidyr::pivot_wider(id_cols =column, 
              names_from = PC, 
              values_from = value) |> 
  select(-1) |>
  as.matrix()

norm((om %*% t(om))-diag(1,5), "F")        
```

