```{.r .cell-code}
broom::augment(km.2, scale(swiss)) %>%
  ggplot() +
  aes(x=Education, y=Infant.Mortality, colour=.cluster) +
  geom_point(aes()) +
  stat_voronoi(data = df_centers.2,
               geom="path"               ) +
  geom_point(data = df_centers.2,
             colour = "black",
             shape="+",
             size=5) +
  coord_fixed() +
  labs(col="Voronoï cells") +
  labs(
    title= "Kmeans over Swiss dataset, k=3"
  ) +
  theme_minimal()
```
