```{.r .cell-code}
whiteside <- whiteside %>% 
  select(-Insul) %>% 
  scale() %>% 
  as_tibble() %>% 
  rename_with(.fn= ~ str_c( ., "scaled", sep="_")) %>% 
  bind_cols(whiteside)

km.2 <- whiteside %>% 
  select(ends_with("scaled")) %>% 
  kmeans(centers=2)


df_centers.2 <-  make_df_centers(km.2)
